package ImportantInterviewQuestion;

public class EvenOrOdd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int number = 8;
		 if(number % 2 == 0)
		 {
			 
			 System.out.println(number + " is Even");
		 }
		 else
		 {
			 
			 System.out.println(number + " is odd");
		 }

	}

}
