package StringConcept;

public class Example2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String name="Testing is important and testing best";
		 System.out.println(name.contains("is Important"));
		 System.out.println(name.equals("Testing is Important and testing best"));
		 System.out.println(name.equalsIgnoreCase("Testing is Important and testing best"));
		 System.out.println(name.startsWith("testing"));

	}

}
